injects.css:
#ct_modal_overlay {
    visibility: hidden;
    opacity: 0;
    position: fixed;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    background: rgba(255,255,255,0.5);
    transition: 0.5s ease all;
    -o-transition: 0.5s ease all;
    -moz-transition: 0.5s ease all;
    -webkit-transition: 0.5s ease all;
    font-size: 16px;
}
#ct_modal_window {
    position: fixed;
    left: 50%;
    top: 50%;
    width: 500px;
    max-height: 400px;
    margin-top: -200px;
    margin-left: -250px;
    background: #ffffff;
    box-shadow: 0 0 25px rgba(0,0,0,0.25);
}
#ct_modal_head {
    padding: 10px;
    font-size: 28px;
    background: #f0f0f0;
    text-align: center;
}
#ct_modal_text {
    padding: 10px;
    max-height: 200px;
    overflow: auto;
    display: block;
}
#ct_modal_input {
    padding: 4px 8px;
    background: white;
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.25) inset;
    width: 300px;
    margin: 10px auto;
    display: block;
    font-size: 22px;
    border: 1px solid #CFCFCF;
}
#ct_button_1, #ct_button_2, #ct_button_3 {
    cursor: pointer;
    box-shadow: 0 3px #999;
    background: #CCC;
    display: inline-block;
    width: 135px;
    padding: 5px;
    margin: 10px 5px;
    text-align: center;
    line-height: 30px;
    border-radius: 3px;
}
#ct_button_1:hover, #ct_button_2:hover, #ct_button_3:hover {
    background: #eee;
}
